import styled from "styled-components";
import Lettering from "../../assets/imgs/lettering.png";

const ContentWrapper = styled.div`
  .Banner {
    position: relative;

    width: 100%;
    height: 500px;
  }

  .Banner img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .BannerTextContainer {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: url(${Lettering});
    background-repeat: no-repeat;
  }

  .IntroText {
    font-size: 1.2rem;
  }

  .VideoPlayer {
    display: flex;
    justify-content: center;
    margin: 20px;
  }

  @media screen and (max-width: 600px) {
    .Banner {
      height: 300px;
    }
  }
`;

export default ContentWrapper;
