import React, { Component } from "react";
import { useParams } from "react-router";
import charList from "../CharactersPage/characterList";
import "../CharactersPage/CharacterPage/CharacterPage.css";
import ContentWrapper from "./HomePage.style.js";

class homePage extends React.Component {
  constructor(props) {
    super(props);
    this.listenScrollEvent = this.listenScrollEvent.bind(this);
    this.state = {
      scrollY: 0,
      velocity: 0.16
    };
  }

  componentDidMount() {
    window.addEventListener("scroll", this.listenScrollEvent, {
      passive: true
    });
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.listenScrollEvent, {
      passive: true
    });
  }

  listenScrollEvent() {
    var scrollTop =
      document.body.scrollTop || document.documentElement.scrollTop;
    var scrolled = scrollTop * this.state.velocity;
    this.setState({ scrollY: scrolled });
  }

  render() {
    return (
      <ContentWrapper>
        <div className="Banner">
          <div className="BannerTextContainer">
            <div className="Text">
              <span>Designed & Developed by Haley Park</span>
              <br></br>
              <span>Inspired by </span>
              <a
                href="https://www.youtube.com/watch?v=Nsi06PG7w_0"
                target="_blank"
              >
                this youtube video
              </a>
            </div>
          </div>
        </div>
        <br />

        <div className="container">
          <h1 className="deco">x</h1>
          <h1>Concierge</h1>

          <div className="row">
            {charList.map((Character, i, arr) => {
              return (
                <>
                  <a
                  // href={socialItem.link}
                  >
                    <img src={Character.photo}></img>
                    {Character.name}
                  </a>
                </>
              );
            })}
          </div>
        </div>
      </ContentWrapper>
    );
  }
}

export default homePage;
